import TransformComponent from "../Components/TransformComponent";
import { Matrix } from "grimoirejs-math";
import ICamera from "./ICamera";
/**
 * Provides perspective camera as implementation of ICamera.
 */
export default class PerspectiveCamera implements ICamera {
    private static _frontOrigin;
    private static _upOrigin;
    private _viewMatrix;
    private _invViewMatrix;
    private _projectionMatrix;
    private _invProjectionMatrix;
    private _projectionViewMatrix;
    private _invProjectionViewMatrix;
    private _far;
    private _near;
    private _fovy;
    private _aspect;
    private _eyeCache;
    private _lookAtCache;
    private _upCache;
    getViewMatrix(): Matrix;
    getInvViewMatrix(): Matrix;
    getProjectionMatrix(): Matrix;
    getInvProjectionMatrix(): Matrix;
    getProjectionViewMatrix(): Matrix;
    getInvProjectionViewMatrix(): Matrix;
    getFar(): number;
    setFar(far: number): void;
    getNear(): number;
    setNear(near: number): void;
    getAspect(): number;
    setAspect(aspect: number): void;
    getFovy(): number;
    setFovy(fov: number): void;
    updateTransform(transform: TransformComponent): void;
    private _recalculateProjection();
}
