import Material from "./Material";
/**
 * Manage factories for materials.
 * Materials can be instanciated with this instance.
 */
export default class MaterialFactory {
    gl: WebGLRenderingContext;
    static factories: {
        [key: string]: ((gl: WebGLRenderingContext) => Material);
    };
    static registerdHandlers: {
        [key: string]: (() => void)[];
    };
    static addMaterialType(typeName: string, factory: (gl: WebGLRenderingContext) => Material): void;
    static addSORTMaterial(typeName: string, source: string): Promise<void>;
    static addSORTMaterialFromURL(typeName: string, url: string): Promise<void>;
    private static _onRegister(factoryName, handler);
    constructor(gl: WebGLRenderingContext);
    instanciate(typeName: string): Promise<Material>;
    private _waitForRegistered(typeName);
}
